mapper.select:
select distinct acm_num from trom where (rom_capa >= to_number(#{person})) and
ROM_NUM not in
#{romNum}

select acm_purl, acm_pname, acm_num, acm_name, acm_city, acm_distr, acm_detailaddr, rep_phone,acm_desc, latitude, longitude, ACM_OPTCODE from ( select /*+INDEX_DESC(to_number(substr(acm_num,2))) */ rownum rn, acm_purl, acm_pname, acm_num, acm_name, acm_city, acm_distr, acm_detailaddr, rep_phone,acm_desc, latitude, longitude, ACM_OPTCODE from tacm where
acm_num in
#{acmNum}
and rownum<= #{cri.pageNum} * #{cri.amount} ) where rn> (#{cri.pageNum}-1) * #{cri.amount}

select count(*) from ( select rownum rn, acm_purl, acm_pname, acm_num, acm_name, acm_city, acm_distr, acm_detailaddr, rep_phone,acm_desc, latitude, longitude, ACM_OPTCODE from tacm where
acm_num in
#{acmNum}
)
