mapper.insert:

	select concat('RV',SEQ_REV.nextval) from dual

INSERT INTO
TREVIEW(PST_NUM,BRD_CODE,P_TITLE,USER_NUM,STISF,ACM_NUM,BOOK_NUM,CONTENT)
VALUES(
#{pstNum},'REV',#{title},#{userNum},#{stisf},#{acmNum},#{bookNum},#{content}
)

mapper.select:
select A.PST_NUM, A.BRD_CODE, a.p_title, a.p_regdate, a.p_updatedate, a.user_num, b.rev_purl, B.STISF, b.acm_num, b.book_num, b.content
FROM TREV_POST A LEFT JOIN trev_detail B ON a.pst_num = b.pst_num
WHERE b.acm_num = #{acmNum}
